import axios from "axios";
import { useNavigate } from "react-router-dom";

const Chat = () => {
  const [message, setMessage] = useState("");
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSend = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://127.0.0.1:8000/chat",
        { message },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setResponse(res.data);
    } catch (err) {
      alert("Session expired or unauthorized");
      localStorage.removeItem("token");
      navigate("/login");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-200 via-purple-200 to-pink-200 flex items-center justify-center p-6">
      <div className="bg-white shadow-2xl rounded-3xl p-10 w-full max-w-3xl border border-purple-300 flex flex-col justify-center">
        <div className="flex justify-between items-center mb-8">
          <h2 className="text-3xl font-extrabold text-purple-700">ðŸ“˜ Markdown Helpdesk Assistant</h2>
          <button
            onClick={handleLogout}
            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md shadow"
          >
            Logout
          </button>
        </div>

        <div className="flex flex-col items-center">
          <textarea
            className="w-full p-4 border rounded-lg mb-6 text-lg shadow-inner focus:outline-none focus:ring-2 focus:ring-purple-400"
            rows="5"
            placeholder="ðŸ’¬ Ask something about your markdown docs..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          ></textarea>

          <button
            onClick={handleSend}
            className="w-full bg-purple-600 hover:bg-purple-700 text-white py-3 rounded-lg text-lg font-medium transition"
            disabled={loading}
          >
            {loading ? "Sending..." : "Send"}
          </button>
        </div>

        {response && (
          <div className="mt-8 bg-gray-50 p-6 rounded-xl border border-gray-300 shadow-sm">
            <p className="text-xl text-gray-800">
              <strong>Answer:</strong><br /> {response.answer}
            </p>
            <p className="text-sm text-gray-600 mt-4">
              <strong>Sources:</strong> {response.sources.join(", ")}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Chat;
